    public int getFileIcon(String fileName, boolean isFileLook) {
        String prefix = FilenameUtils.getExtension(fileName).toLowerCase();
        int rid = context.getResources().getIdentifier(":drawable/big_ico_" + prefix, null, null);
        if(rid <= 0) {
            return rid;
        }
        return rid;
    }
