package cn.cloudartisan.crius.widget;

import android.app.Dialog;
import android.content.Context;
import android.view.View;

import cn.cloudartisan.crius.R;

public class SupplierChooseDialog extends Dialog implements View.OnClickListener {
    Context context;
    SupplierChooseDialog.OnChooseListener operationListener;

    public SupplierChooseDialog(Context context, SupplierChooseDialog.OnChooseListener listener) {
        super(context, R.style.custom_dialog);
        setContentView(R.layout.custom_supplier_dialog);
        findViewById(R.id.meitu_mark).setOnClickListener(this);
        findViewById(R.id.tmall_mark).setOnClickListener(this);
        context = context;
        setCanceledOnTouchOutside(true);
        operationListener = listener;
    }
    
    public void show(String gender) {
        findViewById(R.id.meitu_mark).setVisibility(View.GONE);
        findViewById(R.id.tmall_mark).setVisibility(View.GONE);
        if("1".equals(gender)) {
            findViewById(R.id.meitu_mark).setVisibility(View.VISIBLE);
        }
        if("0".equals(gender)) {
            findViewById(R.id.tmall_mark).setVisibility(View.VISIBLE);
        }
        super.show();
    }
    
    public void onClick(View v) {
        findViewById(R.id.meitu_mark).setVisibility(View.GONE);
        findViewById(R.id.tmall_mark).setVisibility(View.GONE);
        if(v.getId() == R.id.meitu) {
            operationListener.onGenderChecked("1");
            findViewById(R.id.man_mark).setVisibility(View.VISIBLE);
        }
        if(v.getId() == R.id.tmall) {
            operationListener.onGenderChecked("0");
            findViewById(R.id.tmall_mark).setVisibility(View.VISIBLE);
        }
        dismiss();
    }
    public  interface OnChooseListener
    {
         void onGenderChecked(String paramString);
    }
}
